#include<iostream>
#include<vector>

int n,m;

struct kabu{
    
    int a;
    
    int b;
    
    bool operator<(const kabu& another) const {
        return a < another.a;
    };
    
};

std::vector<kabu> vec;

int x;

int res;

int main(){
    
    std::cin>>n>>m;
    
    for(int i=0;i<n;i++){
        std::cin>>x;
        kabu k={x,i+1};
        vec.push_back(k);
    }
    
    std::sort(vec.begin(), vec.end());
    
    for(int i=0;i<vec.size();i++){
        if(m>vec[i].a*vec[i].b){
            res+=vec[i].b;
            m-=vec[i].a*vec[i].b;
        }
        else{
            res+=m/vec[i].a;
            m-=(int)(m/vec[i].a)*vec[i].a;
            break;
        }
    }
    
    std::cout<<res<<std::endl;
    
    return 0;
    
}
