#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>

int main() {
	int N;
	int a[1001], b[1001];
	std::cin >> N;
	for (int i = 0; i < N; i++) {
		std::cin >> a[i] >> b[i];
	}
	std::sort(a, a + N);
	std::sort(b, b + N);
	long long int k = 0;
	for (int i = 0; i < N; i++) {
		k += a[i] * b[N - i - 1];
	}
	std::cout << k << std::endl;
	return 0;
}
