#include <iostream>
#include <stdio.h>
#include <limits.h>

int main() {
	unsigned long long X;
	std::cin >> X;
	int kakko = 2;
	for (unsigned long long  i = 2; i <= X; i++) {
		kakko = kakko * 2;
	}
	kakko = kakko - 1;
	long aaa = kakko % 1000000007;
	std::cout << aaa << std::endl;
	return 0;
}
