int main() {
	cin >> h >> w;
	cin >> n >> m;
	rep(i, m) {
		LL x, y;
		cin >> x >> y;
		f[x][y] = 1;
	}
	queue<Fll>qu;
	dp[0][0][0][0] = 1;
	qu.push(Fll{ 0,0,0,0 });
	while (!qu.empty()) {
		Fll num = qu.front();
		qu.pop();
		LL x = num.first, y = num.second, qq = num.third, flag = num.fourd;
		if (qq > n)continue;
		if (x == h - 1 && y == w - 1)continue;
		rep(i, 4) {
			LL X = x + di[i][0], Y = y + di[i][1];
			if (X >= 0 && X < h&&Y >= 0 && Y < w&&f[X][Y] == 0) {
				if (i > 1) {
					dp[X][Y][qq + 2][0] += dp[x][y][qq][0]+ dp[x][y][qq][1];
					dp[X][Y][qq + 2][0] %= 10000;
				}
				else {
					dp[X][Y][qq][0] += dp[x][y][qq][0] + dp[x][y][qq][1];
					dp[X][Y][qq][0] %= 10000;
				}
				qu.push(Fll{ X,Y,(i > 1 ? qq + 2 : qq),0 });
			}
		}
		if (flag == 0) {
			dp[x][y][qq + 1][1] += dp[x][y][qq][0];
			dp[x][y][qq + 1][1] %= 10000;
			qu.push(Fll{ x,y,qq + 1 ,1 });
		}
	}
	rep(i, n + 1) {
		ans += dp[h - 1][w - 1][i][0];
		ans %= 10000;
	}
	cout << ans << endl;
	return 0;
}
