#include <iostream>
#include <vector>
#include <algorithm>
int main(){
    int n,cnt=0;
    std::cin>>n;
    std::vector<int> a(n),b(n);
    for(int i=0;i<n;++i){
        std::cin>>a[i]>>b[i];
    }
    std::sort(a.begin(),a.end());
    std::sort(b.begin(),b.end());
    for(int i=0;i<n;++i){
        cnt=cnt+a[i]*b[n-i-1];
    }
    std::cout<<cnt<<std::endl;
}
